key = 0
(1..10).each do |k|
	len = 50 + rand(100)
	price = 1 + rand(100000)/100
	puts price
	p = Product.create name: "myProd_#{k}", description: big_string[key,len], price: price
	key += len
	for j in (1..rand(20)) do
		len = 20 + rand(70)
		p.reviews.create rating: 1+rand(5), comment:big_string[key,len]
		key += len
		if key >= big_string.length - 150
			key = 0
		end
	end
end



